UPDATE config_csv AS t
SET alias = v.alias, descript = v.descript
FROM (
    VALUES
    (234, 'db-Preise importieren', 'Die csv-Datei muss folgende Spalten an derselben Position enthalten: id, unit, descript, text, price. - Die Spalte Preis muss numerisch mit zwei Dezimalstellen sein. - Sie können einen Katalognamen für diese Preise wählen, indem Sie eine Importbezeichnung festlegen. '),
    (235, 'Elemente importieren', 'Die csv-Datei muss die folgenden Spalten an derselben Stelle enthalten: Id (arc_id, node_id, connec_id), code, elementcat_id, observ, comment, num_elements, state type (id), workcat_id, verified (Auswahl aus edit_typevalue>value_verified). - Die Felder Observations und Comments sind optional. - ACHTUNG! Import label muss mit dem Typ des Elements (node, arc, connec) gefüllt werden.'),
    (238, 'Einfuhr bei Besuch', 'Um diesen Parameter der import csv Funktion zu verwenden, müssen Sie vor der Ausführung den Systemparameter ''utils_csv2pg_om_visit_parameters'' konfigurieren. Wir empfehlen auch, vorher die Anmerkungen innerhalb der Funktion zu lesen, um so gut wie möglich mit'),
    (384, 'Inp-Kurven importieren', 'Funktion zur Automatisierung des Imports von inp-Kurvendateien. Die csv-Datei muss die folgenden Spalten an gleicher Stelle enthalten: curve_id, x_value, y_value, curve_type (für WS Projekt oder UD Projekt hat curve_type unterschiedliche Werte. Siehe Benutzerhandbuch)'),
    (386, 'Inp-Muster importieren', 'Funktion zur Automatisierung des Imports von inp-Musterdateien. Die csv-Datei muss folgende Spalten an gleicher Stelle enthalten: pattern_id, pattern_type, factor1,.......,factorn. Für WS verwenden Sie bis zu Faktor18, wiederholende Zeilen, wenn Sie möchten. Für UD verwenden Sie bis zu Faktor24. Mehr als eine Zeile für ein Muster ist nicht erlaubt.'),
    (444, 'cat_feature_arc importieren', 'Die csv-Datei muss die folgenden Spalten in genau der gleichen Reihenfolge enthalten: id, system_id, epa_default, code_autofill, shortcut_key, link_path, descript, active'),
    (469, 'Scada-Werte importieren', 'Import von Scada-Werten in die Tabelle ext_rtc_scada_x_data gemäß der Beispieldatei scada_values.csv'),
    (470, 'Aräometer_x_Daten importieren', 'Die csv-Datei muss die folgenden Felder enthalten: hydrometer_id, cat_period_id, sum, value_date (optional), value_type (optional), value_status (optional), value_state (optional)'),
    (471, 'Crm-Periodenwerte importieren', 'Die csv-Datei muss die folgenden Felder enthalten: id, start_date, end_date, period_seconds (optional), code'),
    (445, 'Importieren Sie cat_feature_node', 'Die csv-Datei muss die folgenden Spalten in genau der gleichen Reihenfolge enthalten: id, system_id, epa_default, isarcdivide, isprofilesurface, choose_hemisphere, code_autofill, double_geom, num_arcs, graph_delimiter, shortcut_key, link_path, descript, active'),
    (446, 'Importieren Sie cat_feature_connec', 'Die csv-Datei muss die folgenden Spalten in genau der gleichen Reihenfolge enthalten: id, system_id, epa_default, code_autofill, shortcut_key, link_path, descript, active'),
    (448, 'cat_node importieren', 'Die csv-Datei muss die folgenden Spalten in genau der gleichen Reihenfolge enthalten: id, nodetype_id, matcat_id, pnom, dnom, dint, dext, shape, descript, link, brand, model, svg, estimated_depth, cost_unit, cost, active, label, ischange, acoeff'),
    (449, 'cat_connec importieren', 'Die csv-Datei muss die folgenden Spalten in genau der gleichen Reihenfolge enthalten: id, connectype_id, matcat_id, pnom, dnom, dint, dext, descript, link, brand, model, svg, active, label'),
