    (104, '[{"label": "Operación:", "datatype": "text", "columnname": "exploitation", "widgettype": "combo", "dvquerytext": "Select name as id, name as idval FROM exploitation WHERE expl_id > 0 ORDER by name", "isNullValue": "true", "layoutorder": 1}, {"label": "Dma:", "datatype": "text", "columnname": "dma", "widgettype": "combo", "dvquerytext": "Select name as id, name as idval FROM dma WHERE dma_id != -1 and dma_id!=0 ORDER BY name", "isNullValue": "true", "layoutorder": 2}, {"label": "Desde la Fecha", "datatype": "text", "columnname": "crm_startdate", "filterSign": ">=", "widgettype": "combo", "dvquerytext": "Select start_date::date as id, start_date::date as idval FROM ext_cat_period WHERE id IS NOT NULL ORDER BY start_date", "isNullValue": "false", "layoutorder": 3, "showOnTableModel": {"status": true, "position": 3}}, {"label": "Hasta la fecha", "datatype": "text", "columnname": "crm_enddate", "filterSign": "<=", "widgettype": "combo", "dvquerytext": "Select end_date::date as id, end_date::date as idval FROM ext_cat_period WHERE id IS NOT NULL ORDER BY end_date desc", "isNullValue": "false", "layoutorder": 4, "showOnTableModel": {"status": true, "position": 4}}]')
