INSERT INTO config_report VALUES (102, 'Losses & NRW by Exploitation, Dma & Period', 'SELECT w.exploitation as "Exploitation", w.dma as "Dma", period as "Period", 
total_in::numeric(12,2) as "Total inlet",
total_out::numeric(12,2) as "Total outlet",
total::numeric(12,2) as "Total injected",
auth_bill as "Auth. Bill", auth_unbill as "Auth. Unbill", auth as "Authorized", 
loss_app as "Losses App", loss_real as "Losses Real",loss as "Losses", 
(case when total > 0 then (auth/total)::numeric(12,2) else 0 end) as "Losses Efficiency" ,
rw as "Revenue", nrw as "Non Revenue", 
(case when total > 0 then (rw/total)::numeric(12,2) else 0.00 end) as "Revenue Efficiency",
w.ili::numeric(12,2) as "ILI"
FROM v_om_waterbalance w', '{"orderBy":"1", "orderType": "DESC"}', '[{"columnname":"Exploitation", "label":"Exploitation:", "widgettype":"combo","datatype":"text","layoutorder":1,
"dvquerytext":"Select name as id, name as idval FROM exploitation WHERE expl_id > 0 ORDER by name","isNullValue":"true"},
{"columnname":"Dma", "label":"Dma:", "widgettype":"combo","datatype":"text","layoutorder":2,
"dvquerytext":"Select name as id, name as idval FROM dma WHERE dma_id != -1 and dma_id!=0 ORDER BY name","isNullValue":"true"},
{"columnname":"Period", "label":"Period:", "widgettype":"combo","datatype":"text","layoutorder":1,
"dvquerytext":"Select code as id, code as idval FROM ext_cat_period WHERE id IS NOT NULL ORDER BY code","isNullValue":"true"}]', 'role_om', NULL, true);
INSERT INTO config_report VALUES (104, 'Total Losses & NRW by Dma', 'SELECT n.exploitation as "Exploitation", n.dma as "Dma", 
